import React, {useEffect, useState} from 'react';
import axios from 'axios';
import './App.css';

import Filter from './component/Filter';
import Results from './component/Results';
import { getAllByPlaceholderText } from '@testing-library/react';

function App() {
  const apiUrl="https://sometimes-maybe-flaky-api.gdshive.io/";

  const [state, setState]=useState({
    filterBy:"",
    filter:"",
    results:[],
    selected:{},
    genre:[],
    year:[]
  })

  const getData=(e)=>{
    axios(apiUrl).then(({data})=>{
    var genre=[];
    var year=[];
    data.map((row)=>{
      if(genre.indexOf(row.genre)===-1){
        genre.push(row.genre);
      }
      if(year.indexOf(row.productionYear)===-1){
        year.push(row.productionYear);
      }
    })

    genre.sort();
    year.sort();

    state.results=data;
    state.genre=genre;
    state.year=year;

    // setState(prevState=>{
    //   return{...prevState,results:data,genre:genre,year:year}
    // })
  })

  }

  useEffect(()=>{
    getData();
  })

  const search= (e)=>{
    if(e.key==="Enter"){
      getData();
    }
  }  

  const handleFilter=(e)=>{
    let filterBy=e.target.value;

    setState(prevState=>{
      return{...prevState,filterBy:filterBy}
    });
  }

  return (
    <div className="App">
      <header className="App-header">
        <h1>Movies</h1>
      </header>
      <Filter handleInput={handleFilter} search={search} years={state.year} genres={state.genre}></Filter>
      <Results results={state.results}/>
    </div>
  );
}

export default App;
