import React, {useEffect, useState} from 'react';
import axios from 'axios';
import './App.css';

import Filter from './component/Filter';
import Results from './component/Results';
import { getAllByPlaceholderText } from '@testing-library/react';

function App() {
  const apiUrl="https://sometimes-maybe-flaky-api.gdshive.io/";

  const [state, setState]=useState({
    filterBy:"",
    filter:"",
    results:[],
    selected:{},
    genre:[],
    year:[]
  })

  const getData=(filterBy,filterValue)=>{
    console.log(filterBy);
    console.log(filterValue);
    axios(apiUrl).then(({data})=>{
    var genre=[];
    var year=[];
    var results=[];
    data.map((row)=>{
      if(genre.indexOf(row.genre)===-1){
        genre.push(row.genre);
      }
      if(year.indexOf(row.productionYear)===-1){
        year.push(row.productionYear);
      }
    })

    if(filterBy==="productionYear"){
      console.log("entered filterby prodYears")
      data.map((row)=>{
        if(row.productionYear===filterValue){
          results.push(row);
        }
      })
    }
    else if(filterBy==="genre"){
      data.map((row)=>{
        if(row.genre===filterValue){
          results.push(row);
        }
      })
    }
    else{
      results=data
    }
    console.log(results)

    genre.sort();
    year.sort();

    setState(prevState=>{
      return{...prevState,results:results,genre:genre,year:year}
    })
  })

  }

  useEffect(()=>{
    getData();
  })

  const search= (e)=>{
    if(e.key==="Enter"){
      getData();
    }
  }    

  const search2= (e)=>{
    console.log("entered");
    let filterValue=e.target.value;
    getData(state.filterBy,filterValue);
  }  

  const handleFilter=(e)=>{
    let filterBy=e.target.value;

    setState(prevState=>{
      return{...prevState,filterBy:filterBy}
    });
  }  

  const handleFilter2=(e)=>{
    let filterBy=e.target.value;

    setState(prevState=>{
      return{...prevState,filterBy:filterBy}
    });
    console.log(state)
  }

  return (
    <div className="App">
      <header className="App-header">
        <h1>Movies</h1>
      </header>
      <Filter handleInput={handleFilter} handleInput2={handleFilter2} filterBy={state.filterBy} search={search} search2={search2} years={state.year} genres={state.genre}></Filter>
      <Results results={state.results}/>
    </div>
  );
}

export default App;
