import React, {useEffect, useState} from 'react';
import axios from 'axios';
import './App.css';

import Filter from './component/Filter';
import Results from './component/Results';

function App() {
  const apiUrl="https://sometimes-maybe-flaky-api.gdshive.io/";

  const [state, setState]=useState({
    filterBy:"",
    filter:"",
    results:[],
    selected:{},
    genre:[],
    year:[]
  })

  useEffect(()=>{
    axios(apiUrl).then(({data})=>{
      var genre=[];
      var year=[];
      data.map((row)=>{
        if(genre.indexOf(row.genre)===-1){
          genre.push(row.genre);
        }
        year.push(row.year);
      })
      state.results=data;
      state.genre=genre;
      state.year=year;
      console.log(state);
    })
  })

  const search= (e)=>{
    if(e.key==="Enter"){
      axios(apiUrl).then(({data})=>{
        console.log(data);
        let results=data;
        console.log(data);
      var genre=[];
      var year=[];
      data.map((row)=>{
        genre.push(row.genre);
        year.push(row.year);
      })
      console.log(genre)
        setState(prevState=>{
          return{...prevState,results:results,genre:genre,year:year}
        })
      });
    }
  }  

  const handleFilter=(e)=>{
    let filterBy=e.target.value;

    setState(prevState=>{
      return{...prevState,filterBy:filterBy}
    });
  }

  return (
    <div className="App">
      <header className="App-header">
        <h1>Movies</h1>
      </header>
      <Filter handleInput={handleFilter} search={search} years={state.year} genres={state.genre}></Filter>
      <Results results={state.results}/>
    </div>
  );
}

export default App;
