import React from 'react';

function Details(){

    useEffect(() => {
        console.log(this.props.match.params)
      })

    return (
        <div>
            <img alt={"Poster for "+result.name} src={require("../assets/"+result.image)}/>
            <h2>{result.name}</h2>
            <h4>Production Year: {result.productionYear}</h4>
            <h4>Genre: {result.genre}</h4>
            <p>Synopsis: {result.synopsis}</p>
            <button>Close</button>
        </div>
      );
}

export default Details;
