import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './App.css';

import Filter from './component/Filter';
import Results from './component/Results';

function App() {
  const apiUrl = "https://sometimes-maybe-flaky-api.gdshive.io/";

  const [state, setState] = useState({
    filterBy: "",
    filter: "",
    results: [],
    selected: {},
    genre: [],
    year: [],
    message:""
  })

const search2 = (e) => {
  console.log("entered search");    
  setState(prevState => {
    return { ...prevState, message: "Getting movies info, please wait" }
  });
  let filterValue = e.target.value;
  getData(state.filterBy, filterValue);
}

const handleFilter = (e) => {
  let filterBy = e.target.value;

  setState(prevState => {
    return { ...prevState, filterBy: filterBy }
  });
}

  const getData = async (filterBy, filterValue) => {
    let res = await axios.get(apiUrl);
    console.log(res.data);
    var genre = [];
    var year = [];
    var results = [];
    for (var row of res.data) {
      if (genre.indexOf(row.genre) === -1) {
        genre.push(row.genre);
      }
      if (year.indexOf(row.productionYear) === -1) {
        year.push(row.productionYear);
      }
    }

    genre.sort();
    year.sort();

    if (filterBy === "productionYear") {
      console.log("entered filterby prodYears")
      for (var row of res.data)  {
        if (row.productionYear == filterValue) {
          results.push(row);
        }
      }
      console.log(results)
      setState({
        results: results,
        genre:state.genre,
        year:state.year,
        filterBy:state.filterBy,
        filterValue:filterValue,
        message:""
      })
      console.log(state)
    }
    else if (filterBy === "genre") {
      console.log("entered filterby genre")
      for (var row of res.data)  {
        if (row.genre === filterValue) {
          results.push(row);
        }
      }
      setState({
        results: results,
        genre:state.genre,
        year:state.year,
        filterBy:state.filterBy,
        filterValue:filterValue,
        message:""
      })
      console.log(state)
    }
    else {
      console.log("entered else")
      console.log(state)
      results = res.data
      setState({
        results: results,
        genre:genre,
        year:year,
        message:""
      })
    }
}

useEffect(() => {
  console.log(state)
  if(state.results.length==0){
    console.log("results==0")
    getData();
  }
})

return (
  <div className="App">
    <header className="App-header">
      <h1>Movies</h1>
    </header>
    <Filter handleInput2={handleFilter} filterBy={state.filterBy} filterValue={state.filterValue} search2={search2} years={state.year} genres={state.genre}></Filter>
    <Results results={state.results,state.message} />
  </div>
);
}

export default App;
