import React from 'react';

function Details({result}){
    return (
        <div>
            <img alt={"Poster for "+result.name} src={require("../assets/"+result.image)}/>
            <h2>{selected.name}</h2>
            <h4>Production Year: {selected.productionYear}</h4>
            <h4>Genre: {selected.genre}</h4>
            <p>Synopsis: {selected.synopsis}</p>
            <button>Close</button>
        </div>
      );
}

export default Details;
