  const [state, setState]=useState({
    filterBy:"",
    filter:"",
    results:[],
    selected:{}
  })
  const apiUrl="https://sometimes-maybe-flaky-api.gdshive.io/";

  axios(apiUrl).then(({data})=>{
    setState({results:data});
  });


