import React, { Component } from 'react';
import './App.css';

class App extends Component {
  data=[];
  componentDidMount(){
    const apiUrl = 'https://sometimes-maybe-flaky-api.gdshive.io/';
    fetch(apiUrl)
      .then((response) => response.json())
      .then((data) => this.data=data);

  }
  render(){
    return (
        <div className="App">
          {this.data.map(({name,genre,image,productionYear,synopsis,synopsisShort})=>(
            <img src={image}></img>
          ))}
        </div>
    );
  }
}

export default App;
